import React from 'react'
import {useForm} from 'react-hook-form'
import axios  from 'axios';
import {useNavigate} from 'react-router-dom';


export default function Login({setLogedIn}) {

    const{register,handleSubmit}=useForm();
    const redirect=useNavigate()


    function Logind(data)
    {
        axios.post('http://127.0.0.1:8000/v1/acess/',data).then(response=>{
            setLogedIn(response.data.access)
        redirect('/home')})

    

    }
    

    
    return (
    <>
    <form onSubmit={handleSubmit(Logind)}>
        <label>ENTER USERNAME:</label><br/><br/>
        <input type='text' placeholder='enter username' className='form-control'
        {...register('username')}/><br/><br/>

        <label>ENTER PASSWORD:</label><br/><br/>
        <input type='password' placeholder='enter password' className='form-control'
        {...register('password')}/><br/><br/>

        <input type='submit'value={'Login'} className='btn btn-success'/>


    </form>
    </>
  )
}
